import React, { useState } from "react";
import "./VistaMazo.css";
import Cartas from "../Componentes/Cartas";
import CreaCarta from "../Componentes/CreaCarta";

type CardData = {
  numero: number;
  nombre: string;
  tipo: string;
  ataque: number;
  defensa: number;
  descripcion: string;
  imagen: string;
};

const initialCards: CardData[] = [
  {
    numero: 1,
    nombre: "Goku Ultra instinto",
    tipo: "saiyan",
    ataque: 99999,
    defensa: 99999,
    descripcion: "Vida: 99999",
    imagen: "data:image/jpeg;base64,",
  },
  {
    numero: 2,
    nombre: "Vergeta Super Saiyan",
    tipo: "saiyan",
    ataque: 888,
    defensa: 555,
    descripcion: "Vida: 88888 ",
    imagen: "data:image/jpeg;base64,",
  },
];

function VistaMazo() {
  const [cards, setCards] = useState<CardData[]>(initialCards);

  const handleAddCarta = (nueva: CardData) => {
    // conservar las cartas existentes y agregar la nueva al inicio
    setCards((prev) => [nueva, ...prev]);
  };

  return (
    <div className="mazo-container">
      {/* Componente que abre el modal para crear carta.
          Le pasamos la lista actual para que pueda calcular el número si lo necesita,
          y una función onAddCarta para añadir la carta al estado padre. */}
      <CreaCarta existingCartas={cards} onAddCarta={handleAddCarta} />

      <div className="grid" aria-live="polite">
        {cards.map((c) => (
          <Cartas
            key={c.numero}
            numero={c.numero}
            nombre={c.nombre}
            tipo={c.tipo}
            ataque={c.ataque}
            defensa={c.defensa}
            descripcion={c.descripcion}
            imagen={c.imagen}
          />
        ))}
      </div>
    </div>
  );
}

export default VistaMazo;