import React, { useState } from "react";
import Carta from "../Componentes/Cartas";
import "./VistaMazo.css";

type CardData = {
  numero: number;
  nombre: string;
  tipo: string;
  ataque: number;
  defensa: number;
  descripcion: string;
  imagen: string;
};

function VistaMazo() {
  const cards: CardData[] = [
    {
      numero: 1,
      nombre: "GOKU",
      tipo: "Super Saiyajin",
      ataque: 999,
      defensa: 999,
      descripcion: "El guerrero más insano del universo.",
      imagen: "data:image/jpeg;base64,",
    },
    {
      numero: 2,
      nombre: "Vergueta",
      tipo: "Super Sayajin",
      ataque: 888,
      defensa: 888,
      descripcion: "El segundo en la escala de poder de los Saiyajin.",
      imagen: "data:image/jpeg;base64,",
    },
    {
      numero: 3,
      nombre: "Broly",
      tipo: "Saiyajin",
      ataque: 900,
      defensa: 850,
      descripcion: "Hermano de Goku",
      imagen: "data:image/jpeg;base64,",
    },
  ];

  const [selected, setSelected] = useState<CardData | null>(null);

  return (
    <div className="mazo-container">
      <div className="grid">
        {cards.map((c) => (
          <Carta
            key={c.numero}
            numero={c.numero}
            nombre={c.nombre}
            tipo={c.tipo}
            ataque={c.ataque}
            defensa={c.defensa}
            descripcion={c.descripcion}
            imagen={c.imagen}
            onClick={() => setSelected(c)}
          />
        ))}
      </div>

      {selected && (
        <div className="overlay" onClick={() => setSelected(null)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <button className="close" onClick={() => setSelected(null)}>×</button>
            <div className="modal-card">
              <img src={selected.imagen} alt={selected.nombre} className="big-img" />
              <div className="info">
                <h2>{selected.nombre} (#{selected.numero})</h2>
                <p className="type">Tipo: {selected.tipo}</p>
                <p>Ataque: {selected.ataque}</p>
                <p>Defensa: {selected.defensa}</p>
                <p>{selected.descripcion}</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default VistaMazo;